import NextAuth, {DefaultSession} from "next-auth";

declare module "next-auth" {
    interface Session {
        user: {
            id: string;
            role: "ADMIN" | "EMPLOYEE";
            mustChangePassword: boolean;
        } & DefaultSession["user"];
    }
}
interface User {
    id: string;
    role: "ADMIN" | "EMPLOYEE";
    mustChangePassword: boolean;
}